import { io } from 'socket.io-client';
import { useState, useEffect } from 'react';

const socket = io('http://localhost:3000');

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [nuevoMensaje, setNuevoMensaje] = useState('');
  const [mensajes, setMensajes] = useState([]);

  useEffect(() => {
    socket.on('connect', () => setIsConnected(true));

    socket.on('chat_message', (data) => {
      setMensajes(mensajes => [...mensajes, data]);
    });

    return () => {
      socket.off('connect');
      socket.off('chat_message');
    }
  }, []);

  const enviarMensaje = () => {
    socket.emit('chat_message', {
      usuario: socket.id,
      mensaje: nuevoMensaje
    });
    setNuevoMensaje(''); // Limpiar el campo de entrada después de enviar el mensaje
  }

  return (
    <div className="App">
      <h2>{isConnected ? 'CONECTADO' : 'NO CONECTADO'}</h2>
      <div className="chat-container">
        <div className="chat-messages" style={{ 
          flex: '1', 
          overflowY: 'auto', 
          padding: '10px' 
        }}>
          {mensajes.map((mensaje, index) => (
            <div key={index} className="message" style={{ 
              marginBottom: '10px', 
              padding: '10px', 
              borderRadius: '10px', 
              backgroundColor: mensaje.usuario === socket.id ? '#4CAF50' : '#f2f2f2', 
              color: mensaje.usuario === socket.id ? '#fff' : '#333', 
              alignSelf: mensaje.usuario === socket.id ? 'flex-end' : 'flex-start' 
            }}>
              <p>{mensaje.usuario}: {mensaje.mensaje}</p>
            </div>
          ))}
        </div>
        <div className="input-container" style={{ 
          display: 'flex', 
          alignItems: 'center', 
          padding: '10px' 
        }}>
          <input
            type="text"
            value={nuevoMensaje}
            onChange={e => setNuevoMensaje(e.target.value)}
            placeholder="Escribe un mensaje..."
            style={{ 
              flex: '1', 
              padding: '10px', 
              border: '1px solid #ccc', 
              borderRadius: '25px', 
              marginRight: '10px' 
            }}
          />
          <button onClick={enviarMensaje}>
            Enviar
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
